mapper.insert:
INSERT INTO agents (
    id, name, description, model, prompt, profile_md, context_rounds,
    max_tokens, plugins, knowledge_base, status, created_at, updated_at
) VALUES (
    #{id}, #{name}, #{description}, #{model}, #{prompt}, #{profileMd},
    #{contextRounds}, #{maxTokens}, #{plugins}, #{knowledgeBase}, #{status},
    #{createdAt}, #{updatedAt}
)

mapper.select:
SELECT FROM agents
(name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY updated_at DESC LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
UPDATE agents
name = #{name}, description = #{description}, model = #{model}, prompt = #{prompt}, profile_md = #{profileMd}, context_rounds = #{contextRounds}, max_tokens = #{maxTokens}, plugins = #{plugins}, knowledge_base = #{knowledgeBase}, status = #{status}, updated_at = #{updatedAt},
WHERE id = #{id}
