mapper.insert:
INSERT INTO knowledge_chunk (
    document_id, chunk_index, content, content_hash, token_count, char_count, metadata, created_at
) VALUES (
    #{documentId}, #{chunkIndex}, #{content}, #{contentHash}, #{tokenCount}, #{charCount}, #{metadata}, NOW()
)

mapper.select:
SELECT FROM knowledge_chunk WHERE id IN
#{id}
ORDER BY chunk_index ASC

SELECT COUNT(*)
FROM knowledge_chunk
WHERE document_id = #{documentId}
