mapper.select:
SELECT 
FROM team_members
WHERE team_id = #{teamId} AND user_id = #{userId}

mapper.update:
UPDATE team_members
SET role = #{role}, updated_at = NOW()
WHERE team_id = #{teamId} AND user_id = #{userId}

UPDATE team_members
SET status = #{status}, updated_at = NOW()
WHERE team_id = #{teamId} AND user_id = #{userId}
