mapper.insert:
INSERT INTO user_verification_codes (
    user_id, type, target, code, used, expires_at, created_at
) VALUES (
             #{userId}, #{type}, #{target}, #{code}, #{used}, #{expiresAt}, NOW()
         )

mapper.select:
SELECT 
FROM user_verification_codes
WHERE target = #{target}
AND type = #{type}
AND code = #{code}
AND used = 0
AND expires_at > NOW()
ORDER BY created_at DESC
LIMIT 1

mapper.update:
UPDATE user_verification_codes
SET used = 1
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM user_verification_codes
WHERE target = #{target}
  AND type = #{type}
  AND created_at >= #{sinceTime}
